mapper.select:
SELECT eas.grade, epc.NAME companyName, cs.NAME studentName, cs.id studentId, esc.id studentCompanyId FROM ep_adviser_student eas INNER JOIN ep_student_company esc ON eas.grade = esc.grade AND eas.student_id = esc.student_id AND esc.is_del = 0 LEFT JOIN ep_practice_company epc ON epc.id = esc.company_id AND epc.is_del = 0 LEFT JOIN cc_student cs ON cs.id = eas.student_id WHERE eas.teacher_id = #{teacherId} AND eas.major_id = #{majorId} AND eas.is_del =0
AND epc.NAME = #{companyName}
AND cs.NAME =#{studentName}
order by eas.id

SELECT eas.id adviserStudentId, eas.grade, cs.NAME studentName, cs.id studentId, cs.student_no studentNo, class.NAME className, major.NAME majorName, college.NAME colleageName, epc.NAME companyName FROM ep_adviser_student eas LEFT JOIN cc_student cs ON cs.id = eas.student_id LEFT JOIN sys_office class ON cs.class_id = class.id AND class.type = 3 LEFT JOIN sys_office major ON major.id = class.parentid AND major.type = 2 LEFT JOIN sys_office college ON college.id = major.parentid AND college.type = 1 LEFT JOIN ep_student_company esc ON esc.grade = eas.grade AND esc.student_id = eas.student_id AND esc.is_del = 0 LEFT JOIN ep_practice_company epc ON epc.id = esc.company_id WHERE eas.is_del = 0
AND eas.teacher_id in
#{id}
AND eas.major_id = #{majorId}
AND cs.NAME = #{studentName}
AND eas.grade = #{grade}
ORDER BY eas.grade, cs.id DESC
