mapper.update:
UPDATE ep_practice_report
state = #{state}, teacher_opinion = #{teacherOpinion}, check_date = #{checkDate}, check_id = #{checkId}, submit_date = #{submitDate}, is_del = #{isDel}, is_history = #{isHistory}
WHERE id = #{id}

mapper.select:
SELECT epc.NAME companyName, ct.NAME adviserName, epr.*, eas.id adviserStudentIds, eprd.path FROM ep_adviser_student eas LEFT JOIN cc_teacher ct ON ct.id = eas.teacher_id LEFT JOIN ep_student_company esc ON esc.grade = eas.grade AND esc.student_id = eas.student_id AND esc.is_del = 0 LEFT JOIN ep_practice_company epc ON epc.id = esc.company_id LEFT JOIN ep_practice_report epr ON epr.adviser_student_id = eas.id AND epr.is_del = 0 AND epr.is_history = #{isHistory} left join ep_practice_report_doc eprd on eprd.id=epr.file_id WHERE eas.is_del = 0 AND eas.student_id = #{studentId}
AND epr.is_history = #{isHistory}
order by epr.create_date desc

SELECT eas.grade, cs.id studentId, cs.name studentName, class.name className, epc.NAME companyName, ct.NAME adviserName, epr.*, eas.id adviserStudentIds, eprd.path FROM ep_adviser_student eas LEFT JOIN cc_teacher ct ON ct.id = eas.teacher_id LEFT JOIN ep_student_company esc ON esc.grade = eas.grade AND esc.student_id = eas.student_id AND esc.is_del = 0 LEFT JOIN ep_practice_company epc ON epc.id = esc.company_id LEFT JOIN ep_practice_report epr ON epr.adviser_student_id = eas.id AND epr.is_del = 0 AND epr.is_history =0 left join ep_practice_report_doc eprd on eprd.id=epr.file_id LEFT JOIN cc_student cs ON cs.id = eas.student_id LEFT JOIN sys_office class ON cs.class_id = class.id AND class.type = 3 LEFT JOIN sys_office major ON major.id = class.parentid AND major.type = 2 WHERE eas.is_del = 0 AND eas.teacher_id = #{teacherId}
AND eas.grade= #{grade}
AND epc.NAME = #{companyName}
AND epr.state = #{state}
and (epr.state is null or epr.state=0)
order by eas.grade,cs.id,epr.create_date desc

SELECT ert.grade, cs.id studentId, cs.NAME studentName, class.NAME className, epc.NAME companyName, ct.NAME adviserName, epr.*, eas.id adviserStudentIds, eprd.path FROM ep_reply_teacher ert LEFT JOIN ep_reply_group_student ergs ON ert.major_id = ergs.major_id AND ert.grade = ergs.grade AND ert.group_name = ergs.group_name LEFT JOIN ep_adviser_student eas ON eas.student_id = ergs.student_id AND eas.grade = ergs.grade AND eas.is_del = 0 LEFT JOIN cc_teacher ct ON ct.id = eas.teacher_id LEFT JOIN ep_student_company esc ON esc.grade = eas.grade AND esc.student_id = eas.student_id AND esc.is_del = 0 LEFT JOIN ep_practice_company epc ON epc.id = esc.company_id LEFT JOIN ep_practice_report epr ON epr.adviser_student_id = eas.id AND epr.is_del = 0 AND epr.is_history = 0 LEFT JOIN ep_practice_report_doc eprd ON eprd.id = epr.file_id LEFT JOIN cc_student cs ON cs.id = ergs.student_id LEFT JOIN sys_office class ON cs.class_id = class.id AND class.type = 3 LEFT JOIN sys_office major ON major.id = class.parentid AND major.type = 2 WHERE ert.teacher_id = #{teacherId} and ert.is_del=0
AND ert.grade= #{grade}
AND epc.NAME = #{companyName}
AND epr.state = #{state}
and (epr.state is null or epr.state=0)
order by eas.grade,cs.id desc
