mapper.update:
UPDATE ep_task_book
state = #{state}, modify_date = #{modifyDate}, send_date = #{sendDate}, receive_time = #{receiveTime}, is_history = #{isHistory}
WHERE id = #{id}

mapper.select:
SELECT eas.id adviserStudentId, eas.grade, cs.NAME studentName, cs.id studentId, cs.student_no studentNo, class.NAME className, major.NAME majorName, college.NAME colleageName, etb.id taksBookId, etb.state FROM ep_adviser_student eas LEFT JOIN cc_student cs ON cs.id = eas.student_id LEFT JOIN ep_task_book etb ON etb.adviser_student_id = eas.id AND etb.is_history=0 LEFT JOIN sys_office class ON cs.class_id = class.id AND class.type = 3 LEFT JOIN sys_office major ON major.id = class.parentid AND major.type = 2 LEFT JOIN sys_office college ON college.id = major.parentid AND college.type = 1 WHERE eas.teacher_id = #{teacherId} AND eas.is_del = 0
AND eas.grade = #{grade}
AND etb.state = #{state}
AND etb.state is null
ORDER BY eas.grade,cs.id DESC
